        // =============================================================

        /**
         * \u2605\u2605\u2605\u3010\u65B0\u3057\u3044\u60C5\u5831\u53D6\u5F97\u65B9\u6CD5\u3011\u2605\u2605\u2605
         * \u3082\u306F\u3084API\u306F\u547C\u3070\u306A\u3044\u3002\u30D6\u30E9\u30A6\u30B6\u306E\u8A18\u9332\u4FDD\u7BA1\u5EAB\u304B\u3089\u76F4\u63A5\u3001\u8EAB\u5143\u60C5\u5831\u3092\u8AAD\u307F\u51FA\u3059\u3002
         */
        function loadUserDataFromStorage() {
            const storedUserName = localStorage.getItem('a1_userName');
            const storedUserEmail = localStorage.getItem('a1_userEmail');

            if (storedUserName && storedUserEmail) {
                currentUser.userName = storedUserName;
                currentUser.userEmail = storedUserEmail;
                console.log('\u30E6\u30FC\u30B6\u30FC\u60C5\u5831\u3092\u8A18\u9332\u4FDD\u7BA1\u5EAB\u304B\u3089\u53D6\u5F97:', currentUser);
            } else {
                console.error('\u8A18\u9332\u4FDD\u7BA1\u5EAB\u306B\u30E6\u30FC\u30B6\u30FC\u60C5\u5831\u304C\u898B\u3064\u304B\u308A\u307E\u305B\u3093\u3002\u30ED\u30B0\u30A4\u30F3\u30DA\u30FC\u30B8\u3078\u623B\u308B\u3053\u3068\u3092\u63A8\u5968\u3057\u307E\u3059\u3002');
                // \u5FC5\u8981\u3067\u3042\u308C\u3070\u3001\u3053\u3053\u3067\u30ED\u30B0\u30A4\u30F3\u30DA\u30FC\u30B8\u3078\u5F37\u5236\u9001\u9084\u3059\u308B
                // window.location.href = '/login/';
            }
        }
        
        /**
         * \u2605\u2605\u2605\u3010\u5B8C\u6210\u7248\u3011\u53CE\u96C6\u3057\u305F\u30C7\u30FC\u30BF\u3092Make.com\u306EWebhook\u306B\u9001\u4FE1\u3059\u308B\u95A2\u6570 \u2605\u2605\u2605
         */
        function sendData(toolName, data) {
            // \u5831\u544A\u66F8\u306B\u3001\u53D6\u5F97\u3057\u305F\u30E6\u30FC\u30B6\u30FC\u60C5\u5831\u3092\u8FFD\u8A18\u3059\u308B
            const payload = {
                tool: toolName,
                inputs: data,
                userName: currentUser.userName,
                userEmail: currentUser.userEmail
            };

            fetch(WEBHOOK_URL, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(payload)
            }).catch(error => console.error('Data sending failed:', error));
        }

